import React, { useState } from "react";

const Inputs = (props) => {
	const [check, setCheck] = useState(null);
	const [msgstatus, setMsgstatus] = useState("");
	const [ checked, setChecked] = useState(true)

	const inputdisabled = () => {
		if (check === true) {
			setCheck(false);
			setMsgstatus("");
		} else {
			setCheck(true);
			setMsgstatus("You disabled text input");
		}
	};

	return (
		<div className="container-inp text-center">
			<p>&lt;Input type="text"/&gt;</p>
			<label className="form-label" htmlFor={props.id1}>
				Username
			</label>
			<input
				className={props.clName}
				type={props.type}
				id={props.id1}
				disabled={check}
				placeholder={props.placeholder}
			/>
			<div className="mt-3 text-center">
				<input
					onClick={inputdisabled}
					type="checkbox"
					id={props.id2}
					checked={props.checked}

				/>
				<label className="form-label ml-1" htmlFor={props.id2}>
					Disable input
				</label>
			</div>
			{check === true ? (
				<div className="alert alert-danger mt-3">{msgstatus}</div>
			) : (
				<span></span>
			)}
		</div>
	);
};

export default Inputs;
