import React, { useState } from "react";


const Inputs = (props) => {
	const [checked, setChecked] = useState(null);
	const [msgstatus, setMsgstatus] = useState("");
	const [isInvalid, setIsInvalid] = useState(true);
	const [icon, setIcon] = useState(null)

	const inputdisabled = () => {
		if (checked === true) {
			setChecked(false);
			setMsgstatus("");
		} else {
			setChecked(true);
			setMsgstatus("You disabled text input");
		}
	};

	const invalid = (props) => {
		if (props.clName.includes("is-invalid")) {
			setIsInvalid(true);
		} else {
			setIsInvalid(false);
		}
	};

	const icono = () => {
		if (props.clName.includes("icono")){
			setIcon(true)

	}else{
		setIcon(false)
	}
}


	return (
		<div className="container-inp text-center">
			<p>&lt;{props.inputTitle}/&gt;</p>
			<label className="form-label" htmlFor={props.id1}>
				Username
			</label>

			<div  >
			<span class="input-group-text" id="basic-addon1">{props.icon}</span>
			<input
				className={props.clName}
				type={props.type}
				id={props.id1}
				disabled={checked}
				placeholder={props.placeholder}
			/>
			</div>
			<div className="mt-3 text-center">
				<input onClick={inputdisabled} type="checkbox" id={props.id2} />
				<label className="form-label ml-1" htmlFor={props.id2}>
					Disable input
				</label>
			</div>
			{isInvalid === true ? (
				<div className="invalid-feedback">
					This is a simple default error message
				</div>
			) : (
				<span></span>
			)}
			{checked === true ? (
				<div className="alert alert-danger mt-3">{msgstatus}</div>
			) : (
				<span></span>
			)}
		</div>
	)
}

export default Inputs;
