import React, { useState } from "react";

const Inputs = (props) => {
	const [checked, setChecked] = useState(null);
	const [msgstatus, setMsgstatus] = useState("");
	//const [isInvalid, setIsInvalid] = useState(true);

	const inputdisabled = () => {
		if (checked === true) {
			setChecked(false);
			setMsgstatus("");
		} else {
			setChecked(true);
			setMsgstatus("You disabled text input");
		}
	};

	return (
		<div className="container-inp text-center">
			<p>&lt;{props.inputTitle}/&gt;</p>
			<label className="form-label" htmlFor={props.id1}>
				Username
			</label>
			<div className="input-group mb-3">
  <span className="input-group-text" id="basic-addon1">@</span>
  <input type="text" 
  className="form-control" 
  placeholder="Username" aria-label="Username" aria-describedby="basic-addon1"/>
</div>
			<div className="input-group">
				{props.clName.includes("startIcono") ? (
					<span className="input-group-text">
						{props.icon}
					</span>
				) : (
					<span></span>
				)}
				<input
					className="form-control"
					type={props.type}
					id={props.id1}
					disabled={checked}
					placeholder={props.placeholder}
					value={props.value}
				/>
				{props.clName.includes("endIcono") ? (
					<span className="input-group-text" id="basic-addon1">
						{props.icon}
					</span>
				) : (
					<span></span>
				)}
			</div>
			<div className="mt-3 text-center">
				<input onClick={inputdisabled} type="checkbox" id={props.id2} />
				<label className="form-label ml-1" htmlFor={props.id2}>
					Disable input
				</label>
			</div>
			{props.clName.includes("is-invalid") ? (
				<div className="text-danger">
					This is a simple default error message
				</div>
			) : (
				<span></span>
			)}
			{checked === true ? (
				<div className="alert alert-danger mt-3">{msgstatus}</div>
			) : (
				<span></span>
			)}

		</div>

	);
};

export default Inputs;
