import React, { useState } from "react";

const Inputs = (props) => {

	const [ checked, setChecked ] = useState(null)
	const [ msgstatus, setMsgstatus ] = useState("")


	const inputdisabled = () => {
		if(checked === true){
			setChecked(false)
			setMsgstatus("")
		}else{
			setChecked(true)
			setMsgstatus("You disabled text input")
		}
	}

	return (
		<div>
			<p>&lt;Input type="text"/&gt;</p>
			<div>
			<input
			onClick={inputdisabled} 
			type="checkbox" 
			/>
			<label htmlFor={props.id}>Disable input</label>
			</div>
			<input 
			className={props.clName} 
			type="text" 
			id={props.id} 
			name={props.name} 
			disabled={checked}
			placeholder={props.placeholder}
			/>
			{
				checked === true ? 
				(
					<p className="invalid-feedback">{msgstatus}</p>
				):(
					<span></span>
				)
			}
		</div>
	);
};

export default Inputs;
