import React, { useState } from "react";

const Inputs = (props) => {

	const [ checked, setChecked ] = useState(null)
	const [ msgstatus, setMsgstatus ] = useState("")


	const inputdisabled = () => {
		if(checked === true){
			setChecked(false)
			setMsgstatus("")
		}else{
			setChecked(true)
			setMsgstatus("You disabled text input")
		}
	}

	return (
		<div className="container-inp">
			<p>&lt;Input type="text"/&gt;</p>
			<label className="form-label"  htmlFor={props.id1}>Username</label>
			<input 
			className="form-control" 
			type="text" 
			id={props.id1}
			disabled={checked}
			placeholder={props.placeholder}
			/>
			<div className="mt-3 text-center">
			<input
			onClick={inputdisabled} 
			type="checkbox"
			id={props.id2} 
			/>
			<label className="form-label ml-1" htmlFor={props.id2}>Disable input</label>
			</div>
			{
				checked === true ? 
				(
					<div className="alert alert-danger mt-3">{msgstatus}</div>
				):(
					<span></span>
				)
			}
		</div>
	);
};

export default Inputs;
