import React, { useState } from "react";
import "../../css/style.css";

const Inputs = (props) => {
	const [checked, setChecked] = useState(null);
	const [msgstatus, setMsgstatus] = useState("");

	const inputdisabled = () => {
		if (checked === true) {
			setChecked(false);
			setMsgstatus("");
		} else {
			setChecked(true);
			setMsgstatus("You disabled text input");
		}
	};

	return (
		<div className="container-80 text-center">
			<p>&lt;{props.inputTitle}/&gt;</p>
			<label className="form-label" htmlFor={props.id1}>
				Username
			</label>

			<div className="input-group">
				{props.clName.includes("startIcono") ? (
					<span className="input-group-text">{props.icon}</span>
				) : (
					<span></span>
				)};
				<input 
				className={props.clName} 
				type="text"
				id={props.id1}
				disabled={checked}
				placeholder={props.placeholder}
				defaultValue={props.value}
				/>
				<div className="input-group">
				<input 
				className={props.clName} 
				type="text"
				id={props.id1}
				disabled={checked}
				placeholder={props.placeholder}
				defaultValue={props.value}
				/>
			</div>
				{props.clName.includes("endIcono") ? (
					<span className="input-group-text">
						{props.icon}
					</span>
				) : (
					<span></span>
				)}
			</div>
			<div className="mt-3 text-center">
				<input onClick={inputdisabled} type="checkbox" id={props.id2} />
				<label className="form-label ml-1" htmlFor={props.id2}>
					Disable input
				</label>
			</div>
			{props.clName.includes("is-invalid") ? (
				<div className="text-danger">
					This is a simple default error message
				</div>
			) : (
				<span></span>
			)}
			{checked === true ? (
				<div className="alert alert-danger mt-3">{msgstatus}</div>
			) : (
				<span></span>
			)}
		</div>
	);
};

export default Inputs;
