import React, { useState } from "react";

const Inputs = (props) => {

	const [ checked, setChecked ] = useState(false)

	const inputdisabled = (e) => {
		if(checked === true){
			setChecked(false)
		}else{
			setChecked(true)
		}
	}

	return (
		<div>
			<p>Texto del input</p>
			<div>
			<input
			onClick={inputdisabled} 
			type="checkbox" 
			/>
			<label htmlFor={props.id}>Disable input</label>
			</div>
			<input 
			className={props.clName} 
			type="text" 
			id={props.id} 
			name={props.name} 
			disabled={checked}
			placeholder={props.placeholder}
			/>
		</div>
	);
};

export default Inputs;
